import React, { Component } from 'react';
import Parallax from 'parallax-js'
import './css/style.css';
import './css/default.css';
import './css/responsive.css';
import WebPreLoader from './web_preloader.js';
import WebHeader from './web_header.js';
import OffCanvas from './web_offcanvas.js';
import IndexHome from './web_indexHome';
import IndexService from './web_indexService';
import IndexCarakerja from './web_indexCarakerja';
import IndexJoin from './web_indexJoin';
import IndexContact from './web_indexContact';
import IndexBlog from './web_indexBlog';
import IndexMitra from './web_indexMitra';
import IndexFooter from './web_indexFooter';
import $ from "jquery";

class GarapinWeb extends Component() {
    jQuerycode = () =>{
        function mainMenu() {
            // Variables
            var var_window = $(window),
                navContainer = $('.header-navigation'),
                navbarToggler = $('.navbar-toggler'),
                navMenu = $('.nav-menu'),
                navMenuLi = $('.nav-menu ul li ul li'),
                closeIcon = $('.navbar-close');
            // navbar toggler
            navbarToggler.on('click', function() {
                navbarToggler.toggleClass('active');
                navMenu.toggleClass('menu-on');
            });
            // close icon
            closeIcon.on('click', function() {
                navMenu.removeClass('menu-on');
                navbarToggler.removeClass('active');
            });
            // adds toggle button to li items that have children
            navMenu.find('li a').each(function() {
                if ($(this).next().length > 0) {
                    $(this).parent('li').append('<span class="dd-trigger"><i class="far fa-angle-down"></i></span>');
                }
            });
            // expands the dropdown menu on each click
            navMenu.find('li .dd-trigger').on('click', function(e) {
                e.preventDefault();
                $(this).parent('li').children('ul').stop(true, true).slideToggle(350);
                $(this).parent('li').toggleClass('active');
            });
            // check browser width in real-time
            function breakpointCheck() {
                var windoWidth = window.innerWidth;
                if (windoWidth <= 1199) {
                    navContainer.addClass('breakpoint-on');
                } else {
                    navContainer.removeClass('breakpoint-on');
                }
            }
            breakpointCheck();
            var_window.on('resize', function() {
                breakpointCheck();
            });
        };
        $(".navbar-toggler, .navbar-close,.nav-overlay").on('click', function (e) {
            $(".nav-overlay").toggleClass("active");
        });
        $(".nav-overlay").on('click', function (e) {
            $(".navbar-toggler").removeClass("active");
            $(".nav-menu").removeClass("menu-on");
        });
        //$('.scene').each(function () {
        //    new Parallax($(this)[0]);
        //});
        
    }
    componentDidMount(){
        this.jQuerycode();
        this.parallax = new Parallax(this.scene);
    }
    componentWillUnmount() {
      this.parallax.disable()
    }
    render(){
        render (
        <React.StrictMode>
            <WebPreLoader />
            <WebHeader />
            <OffCanvas />
            <IndexHome />
            <IndexService />
            <IndexCarakerja />
            <IndexJoin />
            <IndexContact />
            <IndexBlog />
            <IndexMitra />
            <IndexFooter />
        </React.StrictMode>
    )
    }
}
export default GarapinWeb;
